var CORE_TEMP = CORE_TEMP || {};

!(function ($) {
    "use strict";
	CORE_TEMP.function = {
		
	};
	
	CORE_TEMP.onReady = {
		init: function () {
			
		}
	};
	
	CORE_TEMP.onLoad = {
		init: function () {
           
        }
	};
	
	CORE_TEMP.initialize = {
        ready: function() {
            for (var init in CORE_TEMP.onReady) {
                CORE_TEMP.onReady[init]();
            }
        },
        load: function() {
            for (var init in CORE_TEMP.onLoad) {
                CORE_TEMP.onLoad[init]();
            }
        }
    };
	
	var $window = $(window),
        $windowheight = $(window).height(),
        $body = $('body');
   
   $(document).on('ready', function () {
        if (typeof TempApp === "undefined") {
            CORE_TEMP.initialize.ready();
			$(document.body).on('click', 'a.woocommerce-contract-offer-link', function(){
				$('.woocommerce-contract-offer').slideToggle(function() {
					let link_toggle = $('.woocommerce-contract-offer-link');

					if ($('.woocommerce-contract-offer').is( ':visible' )) {
						link_toggle.addClass( 'woocommerce-terms-and-conditions-link--open' );
						link_toggle.removeClass( 'woocommerce-terms-and-conditions-link--closed' );
					} else {
						link_toggle.removeClass( 'woocommerce-terms-and-conditions-link--open' );
						link_toggle.addClass( 'woocommerce-terms-and-conditions-link--closed' );
					}
				});
				return false;
			});
			
        }
    });
	
    $window.load(function () {
        if (typeof TempApp === "undefined") {
            CORE_TEMP.initialize.load();
        }
    });
	
})(jQuery);